library(gmp)

tupper <- function(x, y)
{
  yd17 <- as.bigz(y) %/% 17
  ym17 <- as.bigz(y) %% 17

  as.integer(1/2 < as.bigz(as.bigq(yd17)*as.bigq((2^(-17*as.bigz(x)-ym17)))) %% 2)
}

tupper.gen <- function(xs=0:105, ys=0:16,
                       n= as.bigq(""))
{
  dat <- `names<-`(expand.grid(xs, ys), c("x", "y"))
  dat$v <- mapply(function(x, y) tupper(x, y+n), dat$x, dat$y)
  dat
}

tupdat <- tupper.gen()

pdf("tupper.pdf", width=7, height=2)
par(pch=20)
par(pin=c(5,1))
plot(-tupdat$x[tupdat$v==1], -tupdat$y[tupdat$v==1], xlab="", ylab="")
dev.off()
